INSERT INTO LordfilmDB.dbo.MovieDetails (Id, MovieDescription, Country, ProducerId, EngTitle, MovieId, Quality, VideoURL) VALUES (13, N'Сюжет повествует об удивительном фэнтезийном мире, где магия и мифические чудовища поражают и взор, и разум. На своем пути воин столкнется с суровыми испытаниями, станет свидетелем невероятных баталий и будет противостоять загадочным ассасинам в алом. Каждый его шаг должен быть решителен, но осторожен, ведь его действия найдут отклик за пределами реальности и изменят судьбы людей параллельного мира навсегда.', N'Китай', 10, N'Ci sha xiao shuo jia', 11, N'HD 720,1080', N'https://rutube.ru/play/embed/f53580afabe7d2b8e003b07783d5c516/');
